import {InterfaceContent } from "./InterfaceContent.js" ;
import {int,str} from "./Types.js";

interface Config {
  _name:string;
}
export function Interface(config:Config){
  // split all items keys 
  const KEYS:any = Object.keys(config);
  const _interface:Object = {};
  KEYS.forEach((interfaceContent:keyof Config)=> {
    const index: keyof Config = interfaceContent;
    if(index !== '_name'){
      _interface[index] = config[index];
    }
  })
  
  try {
    return function  Validate(_contents:Config) {
      const _outPut:any = {};
      KEYS.forEach((_keys:keyof Config) => {
        const content = _contents[_keys] || null ;
        if(_keys !== "_name"){
          _outPut[_keys] = content; 
        }
        
      });
      
      return _outPut ;
    }
  } catch (e:any){
    console.warn(`there's an error occord while using the ${config._name} Interface`);
  }
}
Interface.Content = InterfaceContent;







