// @params condition  => it is a bolean statement 
// @params ErrHandler => is a string or an object or a function that is displayed whenever condition is not meet 

export function Validate(condition:boolean, ErrHandler:Function):void{
  if(typeof condition !== "boolean"){
    throw new TypeError('conditions must be typeof boolean')
  }
  if(typeof ErrHandler !== "function"){
    throw new TypeError(`errors must be a function or must be a function`)
  }
  

  if(condition === true){
    ErrHandler(condition).onSuccess();
  } else {
    ErrHandler(condition).onError();
  }
}
