// @params condition  => it is a bolean statement 
// @params ErrHandler => is a string or an object or a function that is displayed whenever condition is not meet 

export function Verify(condition:boolean, errText:string):void{
  if(typeof condition !== "boolean"){
    throw new TypeError('conditions must be typeof boolean')
  }
  if(condition !== true){
    throw new Error(errText||"")
  }
}

