export function onlyIf(condition) {
    return {
        execute: function (Func) {
            if (condition === true) {
                Func();
            }
        }
    };
}
onlyIf.not = function (condition) {
    return {
        execute: function (Func) {
            if (condition === false) {
                Func();
            }
        }
    };
};
onlyIf.typeof = function (variables) {
    return {
        is: function (Type) {
            return {
                execute: function (Func) {
                    if (Type.compare(variables) === true) {
                        Func();
                    }
                }
            };
        },
        isnot: function (Type) {
            return {
                execute: function (Func) {
                    if (Type.compare(variables) === false) {
                        Func();
                    }
                }
            };
        }
    };
};
